import React from "react";
import "./App.css";
import Button from '@material-ui/core/Button';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import Paper from '@material-ui/core/Paper';

class Teachers extends React.Component {
  constructor(){
    super() 
      this.state = {
        teacherData: []
      }
    this.getTeacherList();
  }
  getTeacherList() {
    fetch(process.env.REACT_APP_API_URL + "/teachers", { method: 'GET', 
      headers: new Headers({
        'Authorization': "Z8RayolyD8jytCXay2BUE1bktbFAzgsk"
      })  })
      .then(res => res.json())
      .then(
        (result) => {
          console.log("API_URL1=>", process.env.REACT_APP_API_URL);          
          console.log("result=>", result);
          this.setState({teacherData: result});
          // this.setState({
          //   isLoaded: true,
          //   items: result.items
          // });
        },
        // Note: it's important to handle errors here
        // instead of a catch() block so that we don't swallow
        // exceptions from actual bugs in components.
        (error) => {
          this.setState({
            isLoaded: true,
            error
          });
        }
      )
  }
    // Function to remove value to favourite list
  delTeacher = id => e => {
    console.log("idd==>", id);
    fetch(process.env.REACT_APP_API_URL + "/teachers/" + id, { method: 'DELETE', 
      headers: new Headers({
        'Authorization': "Z8RayolyD8jytCXay2BUE1bktbFAzgsk"
      })  })
      .then(res => res.json())
      .then(
        (result) => {
          this.props.history.push('/teachers')
        },
        (error) => {
        }
      )
  this.props.history.push('/teachers')
  }

  render() {
    return (      
      <Paper className="MuiPaper-root-3 MuiPaper-elevation2-7 MuiPaper-rounded-4 SimpleTable-root-1">
      <div component="h2" variant="h1" gutterBottom>
        Teacher List
      </div>
      <a href="\">School List</a>
      <a href="addteachers">Add Teacher</a>
        <Table className="MuiTable-root-30 SimpleTable-table-2">
          <TableHead>
            <TableRow>
              <TableCell>First Name</TableCell>
              <TableCell align="right">Last Name</TableCell>
              <TableCell align="right">School Name</TableCell>
              <TableCell align="right">School Code</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {this.state.teacherData.map(row => (
              <TableRow key={row.id}>
                <TableCell component="th" scope="row">
                  {row.firstName}
                </TableCell>
                <TableCell align="right">{row.lastName}</TableCell>
                <TableCell align="right">{row.school.name}</TableCell>
                <TableCell align="right">{row.school.schoolCode}</TableCell>
                <TableCell scope="roght"><Button variant="outlined" color="secondary" onClick={this.delTeacher(row.id)}>Delete</Button></TableCell>
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </Paper>
    );
  }
}

export default Teachers;
