import React from "react";
import "./App.css";
import Button from '@material-ui/core/Button';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import Paper from '@material-ui/core/Paper';

class ViewTeacher extends React.Component {
  constructor(){
    console.log("constructor called");
    super() 
      this.state = {
        teacherData: []
      }
    this.viewTeacher(69);
  }
  viewTeacher = id => {
    console.log("fn called and id==>", id);
    fetch(process.env.REACT_APP_API_URL + "/teachers/" + id, { method: 'GET', 
      headers: new Headers({
        'Authorization': "Z8RayolyD8jytCXay2BUE1bktbFAzgsk"
      })  })
      .then(res => res.json())
      .then(
        (result) => {
          console.log("Result==>", result);
          this.setState({teacherData: JSON.parse(result)})
        },
        (error) => {
        }
      )
  }
  render() {
    return (      
      <Paper className="MuiPaper-root-3 MuiPaper-elevation2-7 MuiPaper-rounded-4 SimpleTable-root-1">
      <div component="h2" variant="h1">
        Teacher List
      </div>
      <a href="\">School List</a>
      <a href="addteachers">Add Teacher</a>
        <Table className="MuiTable-root-30 SimpleTable-table-2">
          <TableHead>
            <TableRow>
              <TableCell>First Name</TableCell>
              <TableCell align="right">First Name</TableCell>
              <TableCell align="right">Last Name</TableCell>
              <TableCell align="right">School Name</TableCell>
              <TableCell align="right">Country ID</TableCell>
              <TableCell align="right">Region ID</TableCell>
              <TableCell align="right">School Code</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>            
              <TableRow>                
                <TableCell align="right">{this.state.teacherData.firstName}</TableCell>
                <TableCell align="right">{this.state.teacherData.lastName}</TableCell>
                <TableCell align="right">{this.state.teacherData.school.name}</TableCell>
                <TableCell align="right">{this.state.teacherData.school.countryID}</TableCell>
                <TableCell align="right">{this.state.teacherData.school.regionID}</TableCell>
                <TableCell align="right">{this.state.teacherData.school.schoolCode}</TableCell>
              </TableRow>
          </TableBody>
        </Table>
      </Paper>
    );
  }
}

export default ViewTeacher;
